import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [colorizedUrl, setColorizedUrl] = useState(null);
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Handle file selection
  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
    setColorizedUrl(null);
    setError(null);
  };

  // Handle file upload
  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) return;
    setLoading(true);
    setError(null);
    try {
      const formData = new FormData();
      formData.append('file', selectedFile);
      const res = await axios.post('http://127.0.0.1:8000/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      const colorizedFilename = res.data.colorized_filename;
      setColorizedUrl(`http://127.0.0.1:8000/download/${colorizedFilename}?type=colorized`);
      fetchHistory();
    } catch (err) {
      setError('Upload failed.');
    }
    setLoading(false);
  };

  // Fetch upload history
  const fetchHistory = async () => {
    try {
      const res = await axios.get('http://127.0.0.1:8000/history');
      setHistory(res.data);
    } catch (err) {
      setHistory([]);
    }
  };

  React.useEffect(() => {
    fetchHistory();
  }, []);

  return (
    <div className="App">
      <h1>Image Colorization App</h1>
      <form onSubmit={handleUpload}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <button type="submit" disabled={loading || !selectedFile}>Upload & Colorize</button>
      </form>
      {loading && <p>Processing...</p>}
      {error && <p style={{color: 'red'}}>{error}</p>}
      {colorizedUrl && (
        <div>
          <h2>Colorized Image</h2>
          <img src={colorizedUrl} alt="Colorized" style={{maxWidth: '400px', margin: '1em 0'}} />
          <a href={colorizedUrl} download>Download Colorized Image</a>
        </div>
      )}
      <h2>Upload History</h2>
      <ul>
        {history.map((item, idx) => (
          <li key={idx}>
            {item.original_filename} - {new Date(item.upload_time).toLocaleString()} -
            <a href={`http://127.0.0.1:8000/download/${item.saved_filename}?type=upload`} target="_blank" rel="noopener noreferrer">Original</a> |
            <a href={`http://127.0.0.1:8000/download/${item.colorized_filename}?type=colorized`} target="_blank" rel="noopener noreferrer">Colorized</a>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
